window.__NUXT__=(function(a,b,c,d,e,f){return {staticAssetsBase:"\u002F_nuxt\u002Fstatic\u002F1610953063",layout:"default",error:null,serverRendered:true,routePath:"\u002Far\u002Fservices",config:{},__i18n:{langs:{ar:{nav:{title:"فـن المعمار للصيانة",home:"الرئيسية",services:"الخدمات",portfolio:"مشاريع",about:"عن الشركة"},heading:{first:{intro:"شــركة",title:"فـن المعمار السعودية",message1:"شركة فن المعمار السعودية للصيانة المحدودة",message2:"إحدى شركات مجموعة الخولي"},second:{intro:"ما يمكننا فعله؟",title:"نقوم بالبناء والصيانة",message:"إحدى الشركات الرائدة في مجال البناء والصيانة في المملكة"},third:{title:"شركة فن المعمار للإنشاءات",message:"نحن نبني أحلامك في الواقع ونحافظ عليها."},fourth:{title:"شركة فن المعمار السعودية للصيانة",message:"نقدم خدمات إحترافية في مجال صيانة المباني لمختلف القطاعات داخل المجتمع"}},intro:{title1:"الأعلى تقييماً",body1:"شركة فن المعمار، تعد واحدة من أعلى الشركات تقييماً في المملكة العربية السعودية.",title2:"أعلى جودة ممكنة",body2:"تحرص شركة فن المعمار السعودية للصيانة المحدودة على أن تقدم أعلى جودة ممكنة في جميع الأعمال الموكلة إليها. وذلك عن طريق إتباع أحدث الطرق في إدارة المشاريع والحرص على إقتناء أفضل الكفاءات.",title3:"أقل الأسعار",body3:"واحدة من أهم الأهداف لشركتنا هي تقديم الخدمات بأقل التكاليف مع الحفاظ على أعلى جودة ممكنة."},home:{services:{title:"خدمات الشركة",sections:{facility:{title:"إدارة المرافق وتشغيلها",body:"نقدم خدمات صيانة المباني لمختلف المرافق المجتمعية، والجامعات، والوزارات، والمتاحف، والمؤسسات العامة والخاصة، من خلال أعمال الصيانة المنتظمة والمجدولة، مما يمنح عملائنا خدمة كاملة وراحة البال.",btn:c},constructions:{title:"الإنشاءات وإعادة التأهيل",body:"نقدم خدمات الإنشاءات وخدمات إعادة التأهيل من خلال مهندسينا المؤهلون في جميع المجالات الهندسية، ولدينا سجل حافل في الإنشاءات في المملكة.",btn:c},electrical:{title:"خدمات قطاع الكهرباء",body:"نقدم خدمات الصيانة الكهربائية من خلال مهندسينا المحترفين والفنيين المهرة القادرين على أداء أعمال الصيانة الكهربائية بطريقة مناسبة لعملائنا.",btn:c},mechanical:{title:"الخدمات الميكانيكية",body:"نقدم خدمات الصيانة الميكانيكية من خلال مهندسينا المحترفين والفنيين المهرة القادرين على أداء أعمال الصيانة الميكانيكية بأفضل الطرق المناسبة لعملائنا.",btn:c},landscaping:{title:"تنسيق الحدائق",body1:"نقدم خدمات تنسيق الحدائق الكاملة بدأً من تصميم المشروع، والخدمات الخارجية، والداخلية إلى تركيب نظام الري. ويتم التعامل مع تصميم المناظر الطبيعية من قبل فريق متخصص من المحترفين الذين يقومون بإجراء تحليل كامل للموقع والتوصل إلى أمثل الحلول لتحويل منطقتك الخارجية إلى حديقة رائعة.",body2:" نستخدم جميع منتجات التسميد ومكافحة الآفات والمعدات الزراعية الحديثة وفقًا لأعلى المعايير التي يتطلبها القانون وجميع أعمالنا يتم الإشراف والموافقة عليها من قبل مهندسون مؤهلون تأهيلا عاليا. يضمن تركيب نظام الري لدينا أن تحصل جميع نباتاتك الخارجية على الكمية المناسبة من الماء في أفضل وقت ممكن.",btn:c},pool:{title:"صيانة حمامات السباحة",body:"نقدم خدمات تنظيف حمامات السباحة في القطاعين العام والخاص. نستخدم أفضل منتجات ومعدات التنظيف. مع فنيينا المحترفون، يمكنك أن تطمئن إلى أن حمام السباحة الخاص بك نظيف دائمًا ويحتوي على أفضل المواد الكيميائية للتنظيف، وأفضل معدات النظافة. نقدم أيضًا خدمات الصيانة والتجديد لحمامات السباحة لجعلها تبدو أفضل دائمًا.",btn:c}}},projects:{title:"أحدث المشروعات",types:{all:"مشروعاتنا",one:"مشروعات الصيانة",two:"مشروعات الإنشاءات"},proj1:{title:"جامعة الإمام",body:{p1:"تأسست عام 1953. وفي عام 1974 تم منحها صفة الجامعة بمرسوم ملكي. تضم الجامعة 10 كليات. تضم حاليًا أكثر من 24000 طالب و 1300 عضو هيئة تدريس ولديها معاهد إسلامية متوسطة وثانوية في المدن الرئيسية في المملكة العربية السعودية وإندونيسيا وجيبوتي واليابان.",p2:"تهدف الجامعة كمؤسسة ثقافية وفكرية إلى تعزيز تقديم المعرفة للدراسات الجامعية والدراسات العليا.",p3:"من أهم الأهداف تعزيز الترجمة والنشر حيث تمتلك الجامعة دار طباعة خاصة بها. علاوة على ذلك، فإن الجامعة تحقق أهدافها في إطار الشريعة الإسلامية.",p4:"تعتبر الجامعة من أكبر مشاريعنا في المملكة العربية السعودية، فهي تحتوي على أكثر من 2000 موظف من موظفينا المحترفون، عمال النظافة، الفنيين، المهندسين، المحاسبين ، ...",p5:"نحن مسؤولون عن صيانة وتشغيل وتنظيف الحرم الجامعي."},btn:a},proj2:{title:"جامعة الباحة",body:{p1:"جامعة حكومية تأسست عام 2006. ويرأسها الأستاذ الدكتور سعد بن محمد الحركي.",p2:"يقع الحرم الرئيسي في العقيق على بعد 25 كم من مدينة الباحة. كما تقع مواقع أخرى للحرم الجامعي في المخواة والمندق وبلجرشي.",p3:"أرض الحرم الرئيسي في العقيق تشغل حوالي 6.7 كيلومتر مربع.",p4:"نحن نخدم شركة الباحة من خلال موظفينا المحترفين، كمزودي الصيانة والعمليات."},btn:a},proj3:{title:"مركز الملك عبدالعزيز التاريخي",body:{p1:"مركز الملك عبد العزيز التاريخي هو منطقة في مدينة الرياض ، عاصمة المملكة العربية السعودية.",p2:"وهو 'المركز التاريخي' للمدينة حيث يقع إلى الجنوب حول قلعة المصمك ومسجد الجمعة الرئيسي في منطقة الديرة.",p3:"أصل مركز الملك عبد العزيز التاريخي هو المجمع السابق لقصر المربع الذي شيده الملك عبد العزيز عام 1936\u002F1937 على بعد كيلومتر ونصف إلى الشمال من المدينة القديمة وخارج ما كان آنذاك، أسوار المدينة.",p4:"يقوم مهندسونا وفنيونا وعمال النظافة لدينا بتزويد المركز بخدمات الصيانة والتشغيل."},btn:a},proj4:{title:"مكتبة الملك فهد الوطنية",body:{p1:"تأسست المكتبة عام 1990 ، ومقرها مدينة الرياض. ومعروفة بتصميم المبنى الرائع والكتب النادرة.",p2:"لسنوات عديدة خدمنا المكتبة بخبراتنا في مجال الصيانة والعمليات."},btn:a},proj5:{title:"الرئاسة العامة لرعاية الشباب",body:{p1:"قامت الرئاسة العامة لرعاية الشباب (GPYW) برعاية مجموعة كبيرة من الأنشطة الثقافية.",p2:"وشملت هذه الأنشطة الفنون، والحرف ، ونوادي الأدب والدراما والفلكلور.",p3:"لعبت الرئاسة العامة لرعاية الشباب دورًا نشطًا في الأحداث الثقافية الوطنية والدولية ، بما في ذلك المعارض الفنية ومسابقات الخط والشعر والمقالات.",p4:"نحن مسؤولون عن تصميم وبناء التوسعة للنادي الاجتماعي في منطقة سكن أعضاء هيئة التدريس."},btn:a},proj6:{title:"إستاد الأمير عبد الله الفيصل",body:{p1:"يقع الملعب جنوب شرق مدينة جدة بين جامعة الملك عبد العزيز والمدينة الصناعية (حي الوزيرية).",p2:"تم بناء الاستاد في عام 1970 ، ويتسع لحوالي 27000 متفرج ، وهو جزء من مجمع رياضي بلدي يضم ساحة داخلية ومركزًا للألعاب المائية.",p3:"يتكون الملعب من مدرجين رئيسيين. الجناح الغربي المغطى ، مجالس بالكامل وبه صندوق مغلق لكبار الشخصيات وأجنحة إعلامية في منبر مركزي.",p4:"الحامل الشرقي على شكل قوس متدرج ، ومنحنيات على طول مسار الجري. توجد لوحة نتائج كبيرة واحدة على الحافة الشمالية من الجناح الشرقي.",p5:"نحن مسؤولون عن تصميم التوسعة الجديدة للملعب وبنائها من خلال مهندسينا وعمالنا المحترفين."},btn:a},proj7:{title:"نادي هجر الرياضي",body:{p1:"تأسس نادي هجر في مدينة الهفوف التابعة لمحافظة الأحساء عام 1948 وهو أقدم نادي كرة قدم للمحترفين في منطقة الأحساء.",p2:"تم الاعتراف بالنادي رسميًا كنادي كرة قدم للمحترفين في عام 1950. كان النادي يُطلق عليه سابقًا 'نادي البدر' قبل تغيير اسمه إلى 'هجر'.",p3:"نحن مسؤولون عن تصميم وبناء وإعداد النادي."},btn:a},proj8:{title:"مستشفى الملك خالد الجامعي",body:{p1:"مرفق متعدد التخصصات في الخدمات الطبية العامة والمتخصصة التي تقدم الرعاية الأولية والثانوية لمرضاها.",p2:"ويشمل مرفقًا مخصصًا للمرضى، وأحدث الخدمات الجراحية ومختبرًا مجهزًا بالكامل وموظفين، وخدمات الأشعة والصيدلة بالإضافة إلى خدمات الدعم الأخرى وبرنامج الرعاية الصحية المنزلية المخصص.",p3:"نحن مسؤولون عن توسيع مركز غسيل الكلى وتوسيع غرفة الطوارئ في مستشفى الجامعة."},btn:a},proj9:{title:"كلية الملك فهد الأمنية",body:{p1:"كلية الملك فهد الأمنية معهد تعليمي \u002F عسكري تابع لوزارة الداخلية السعودية ، تقع الكلية في شرق الرياض. كلية تثقيف وتدريب للطلاب حفاظاً على الأمن والاستقرار في البلاد.",p2:"نوفر للكلية فنيين خبراء في الصيانة والعمليات."},btn:a},proj10:{title:"جامعة الأمير سطام بن عبد العزيز",body:{p1:"كانت جامعة الأمير سطام بن عبد العزيز تعرف سابقًا باسم جامعة الأمير سلمان بن عبد العزيز أو جامعة الخرج. هي جامعة سعودية تقع في مدينة الخرج.",p2:"وهي تحت إشراف وزارة التعليم العالي بالمملكة العربية السعودية ويديرها الدكتور عبد الرحمن بن محمد العصيمي.",p3:"نوفر للحرم الجامعي والسكن الجامعي والمستشفى الجامعي خدماتنا المهنية في الصيانة والتشغيل والنظافة."},btn:a},proj11:{title:"طريق الملك فهد",body:{p1:"يقع طريق الملك فهد في قلب مدينة الرياض.",p2:"يحافظ المتخصصون لدينا في الصيانة والعمليات على نظافة الشوارع الطويلة المزدحمة وأن تكون دائماً في حالة جيدة."},btn:a},proj12:{title:"الحي الدبلوماسي",body:{p1:"تم بناء الحي الدبلوماسي في الأصل لاستيعاب جميع موظفي السفارات الأجنبية والدبلوماسيين، ولكن في الوقت الحاضر يمكن لأي شخص العيش هناك في واحدة من العديد من المناطق السكنية.",p2:"الحي كبير جدًا لدرجة أنه يشبه مدينة صغيرة بجوار الرياض. يمكن الوصول إلى الأحياء الدبلوماسية بسهولة من وسط المدينة في غضون 10 دقائق باتباع طريق العروبة حتى نهايته.",p3:"نوفر للحي الدبلوماسي كافة المتخصصين لدينا في الصيانة والعمليات."},btn:a}},lets:{body:"فلنبدأ بالعمل معاً!"},count:{body:"عملاء راضون!"},clients:{title:"عملائنا",body:{p1:"عملاؤنا، مثل موظفينا، هم أغلى ما نملك. نحن نرعى علاقة كل عميل بالالتزام والعاطفة والنزاهة، وهذا هو السبب في أن معظم عملائنا كانوا معنا على مدار عقود من وجودنا.",p2:"من خلال بناء ليس فقط علاقات تجارية ولكن شراكات طويلة الأمد مع عملائنا، نحصل على فرصة للعمل عن كثب معهم.",p3:"يساعدنا ذلك على فهم احتياجات أعمالهم الفريدة، ومن ثم نتمكن من تخصيص حلولنا ومنتجاتنا لتلبية أهداف أعمالهم طويلة الأجل.",p4:"بكل فخر يمكننا الاستشهاد بعملائنا، والذين يعدون من أكبر الكيانات في المملكة. نحن فخورون جداً لأننا ساهمنا في نمو العديد من هذه الكيانات.",p5:"نحن نقدم لعملائنا أعلى جودة من خدمات وحلول الصيانة والعمليات والإنشاءات، ونعمل على سياسات التحسين المستمر لمنتجاتنا وعملياتنا والأفراد لدينا في محاولة لتقديم حلول وخدمات استثنائية وتجربة تتجاوز توقعات العميل.",p6:"للقيام بذلك، نسعى جاهدين لتحقيق التوازن بين المرونة في طريقة عملنا والتحكم الصارم لتلبية احتياجات العملاء باستمرار."}}},footer:{title:"تواصل معنا",info:{intro:"يمكنك التواصل معنا على أي من قنوات الاتصال أدناه.",address:"18 شارع زبيدة بنت جعفر، حي المربع، الرياض، المملكة العربية السعودية",phone1:"966114041919+",phone2:"966114053012+",mail:e},form:{name:"الإسم",mNumber:"رقم الهاتف",mAddress:"عنوان البريد الإلكتروني",msg:"الرسالة",send:"إرســـال",errors:{name:"هذا الحقل مطلوب.",mail:"برجاء إدخال بريد إلكتروني صحيح.",mobile:"هذا الحقل يجب أن يحتوي على أرقام فقط، إستخدم صفرين بدلاً من علامة (+)."}}},about:{story:{title:"قصــتنا",subTitle:"تفتخر شركتنا بـ 40 عامًا من الخدمة في المملكة العربية السعودية.",list:{li1:"منذ التأسيس في عام 1978 (1398 هـ)، نمت أعداد الموظفين المحترفين لدينا إلى 3000 موظف موزعون في جميع مشاريعنا. تعتبر شركتنا من الشركات الرائدة في مجال الصيانة والإنشاءات في المملكة.",li2:"نحن فخورون أيضًا بمساهمتنا الكبيرة في تطوير مجتمعنا الحديث من خلال مساهمتنا في عدد كبير من مشاريع البناء والنقل والمياه والصرف الصحي والبيئة والصحة والتعليم.",li3:"من خلال الارتباط بشركاء رائدين وعالميين، لدينا العديد من المشاريع المهمة والمميزة، حيث عملنا في بعض الأحيان لتسريع الجداول الزمنية لتلبية الأهداف الأهم لعملائنا.",li4:"مع قيامنا بتوسيع وجودنا الجغرافي بشكل كبير في جميع أنحاء المملكة، نشعر بالثقة في القاعدة القوية والخبرة التي تحصلنا عليها لخدمة ودعم عملياتنا."}},whyUs:{title:"لماذا تختارنا!",list:{li1:"نحن نحترم عملائنا، ونقدر أولئك الذين وثقوا بنا من خلال عمل متكرر مرة بعد مرة ومشروع بعد مشروع.",li2:"نحن نقدر موظفينا المتفانين وزملائنا المخلصين الذين ساعدتنا مواهبهم على النمو لنصبح الشركة الحديثة التي نحن عليها اليوم.",li3:"نحن نحترم الشركات الشريكة التي شاركتنا نمونا ونجاحنا، والعديد منها قادة في قطاعاتهم.",li4:"معكم جميعًا نتطلع إلى مستقبل أفضل قائم على ماضٍ عريق."},vision:{title:"رؤيتــنا",body:"مصدر إلهامنا هو رغبتنا في التميز كقادة إقليميين في الصيانة والعمليات والإنشاءات، ومهمتنا هي الحفاظ على علاقة طويلة الأمد مع عملائنا."},mission:{title:"مهمتنــا",body:"تقديم التميز في مجال الصيانة والعمليات والإنشاءات، والحفاظ على التزامنا إتجاه عملاؤنا، وتجاوز توقعاتهم."}},sisters:{title:"شركات شقيقة",comp01:"العربية للمشروعات",comp02:"فن المعمار للمصاعد",comp03:"مؤسسة حمزة الخولي",comp04:"الشركة المتحدة للمشروعات"},chairman:{title:"كلمة الرئيس",list:{li1:"تم بناء شركتنا على أساس القيم التي يتم مشاركتها عبر كل عضو من أعضاء فريقنا.",li2:"ساعدت هذه القيم الأساسية على نمو الشركة، لتصبح شركة وطنية رائدة في خدمات الصيانة والإنشاءات.",li3:"نحن نتفهم أن أهم العناصر في صناعتنا هي السلامة والوقت والتكلفة والجودة، مع حماية بيئتنا والحفاظ عليها.",li4:"مع وضع ذلك في الاعتبار، تتعاون شركتنا مع عملائنا لتقديم أفضل نتيجة ممكنة.",li5:"نحن مدفوعون لتحقيق النجاح من خلال التعاون، القائم على النزاهة والرضا، وبناء علاقات غير مسبوقة، وخلق حلول مبتكرة ومستدامة."}},lets:{body:"الجودة تأتي أولاً!"}}},en:{nav:{title:f,home:"Home",services:"Services",portfolio:"Portfolio",about:"About"},heading:{first:{intro:"We Are",title:f,message1:"Saudi Building Technic Maintenance Company (SBTMC)",message2:"One of the main companies at Alkholi Group."},second:{intro:"What we can do?",title:"We Build and Maintain",message:"A leading company in maintenance and construction fields in KSA."},third:{title:"BuildingTek Construction",message:"We construct your dreams in life, and maintain it."},fourth:{title:"BuildingTek Maintenance",message:"We provide a professional buildings maintenance services for various community facilities."}},intro:{title1:"Top Rated",body1:"Saudi Building Technic is recognized as one of the leading maintenance and constructions firms in the kingdom.",title2:"Best Quality",body2:"Our company always make sure to provide the highest quality possible by following the latest project management techniques, and heiring the talented and most qualified employees.",title3:"Low Prices",body3:"One of the main goals for our company is to provide our services at low price while maintaining the highest quality."},home:{services:{title:"Our Services",sections:{facility:{title:"Facility Management, Operation, & Maintenance",body:"We provide a professional buildings maintenance services for various community facilities, Universities, Ministries, Museums, Public and Private institutions, through regular and scheduled maintenance works giving our customers total service and peace of mind.",btn:d},constructions:{title:"Constructions & Rehabilitations",body:"We provide constructions services, and rehabilitations services, through our professional top class engineers, in all engineering fields, we have a great record in constructions in the kingdom.",btn:d},electrical:{title:"Electrical",body:"We provides electrical maintenance services through our professional engineers and skilled technicians who are able to perform electrical maintenance works in a very proper manner to our clients.",btn:d},mechanical:{title:"Mechanical",body:"We provides mechanical maintenance services through our professional engineers and skilled technicians who are able to perform mechanical maintenance works in a very proper manner to our clients.",btn:d},landscaping:{title:"Landscaping",body1:"We provide full landscaping services from project design, outdoor and indoor services to irrigation system installation. Landscaping design is handled by a dedicated team of professionals that makes a complete analysis of the site and comes up with solutions to transform your outdoor areas into an outstanding garden.",body2:"Our indoor and outdoor landscaping services use all the fertilization and pest control products according to the highest standards required by law, modern agricultural equipment and all our work is supervised and approved by a highly qualified Engineer. Our irrigation system installation ensures your property that all your outdoor plants and scenery get the right quantity of water at the best moment possible.",btn:d},pool:{title:"Pool Cleaning and Maintenance",body:"We provide a cleaning services for swimming pools, in both public and private sectors. We use the best cleaning products and equipment. With our professional technician you can rest assure that your swimming pool is always clean and has the best cleaning chemicals, and the best cleaning equipment’s. We also provide a maintenance and renovation services for swimming pools to make it always looks better, and feels better.",btn:d}}},projects:{title:"Latest Projects",types:{all:"All Projects",one:"Maintenance",two:"Construction"},proj1:{title:"Imam University",body:{p1:"Was founded in 1953. In 1974, it was granted university status by royal decree.The university houses 10 faculties. It currently has more than 24,000 students and 1,300 faculty.It has intermediate and secondary Islamic institutes in major cities of Saudi Arabia, Indonesia, Djibouti and Japan.",p2:"The university as a cultural and intellectual institution aims to promote knowledge offering undergraduate as well as postgraduate studies.",p3:"One of the most important aims is to promote translating and publishing as the university has its own print house. Moreover, the university achieves its goals within the Islamic Shari'a.",p4:"The university is one of our largest project in the kingdom of Saudi Arabia, it contains more than 2000 of our professional, cleaners, technicians, engineers, IT, accountants,...",p5:"We are responsible of the university campus maintenance, operations and cleaning."},btn:b},proj2:{title:"Al Baha University",body:{p1:"It is a public university that was founded in 2006. The president of the university is Prof. Dr. Saad bin Mohammad Alhareky.",p2:"The main campus is at Alaqiq about 25 km away from Al Baha city. The other campuses are in Almikhwah, Almandaq, and Baljurashi.",p3:"The land of the main campus in Alaqiq occupies about 6.7 km2.",p4:"We serve Al Baha company through our professional staff, as a maintenance and operations providers."},btn:b},proj3:{title:"King Abdulaziz Historical Center",body:{p1:"The King Abdul Aziz Historical Centre is a region in the city of Riyadh, the capital of Saudi Arabia.",p2:"It is not the \"historic center\" of the city as this lies to the south around Masmak fort and the main Friday Mosque in the Dira district.",p3:"The origin of the King Abdul Aziz Historical Centre is the former compound of the Murabba' Palace, that was built in 1936\u002F37 by King Abdul Aziz about one and a half kilometers to the north of the old city and well outside of the then still existing city walls.",p4:"Our engineers, technicians, and cleaners provide the center with the maintenance and operations services."},btn:b},proj4:{title:"King Fahad National Library",body:{p1:"The library was established in 1990, and is located in Riyadh. known with its splendid building design and rare books.",p2:"For many years we served the library with our expertise in maintenance and operations services."},btn:b},proj5:{title:"General Presidency of Youth Welfare",body:{p1:"The General Presidency of Youth Welfare (GPYW) has sponsored a range of cultural activities.",p2:"These have included arts and crafts, and literary, drama and folklore clubs.",p3:"The General Presidency of Youth Welfare has played an active role in national and international cultural events, including arts exhibitions, calligraphy and poetry and essay competitions.",p4:"We are responsible of the design, construction of the extension to the social club in the faculty housing region."},btn:b},proj6:{title:"Abdullah Al Faisal Stadium",body:{p1:"The stadium is located in the south east of Jeddah, between King Abdulaziz University and the industrial city (Al Waziriah District).",p2:"The stadium was constructed in 1970, and has a capacity of around 27,000 spectators, and is part of a municipal sports complex that includes an indoor arena and an aquatics center.",p3:"The stadium consists of two main stands. The covered west stand, is fully seated and has an enclosed VIP box and media suites in a central tribune.",p4:"The arch shaped east stand is terraced, and curves along the running track. A single large scoreboard is located on the northern edge of the east stand.",p5:"We are responsible of the expansion of the stadium design and constructions, through our professional engineers and labors."},btn:b},proj7:{title:"Hajir Sport Club",body:{p1:"Hajer Club were founded in the city of Hofuf part of the Al-Ahsa province in 1948 and they are the oldest professional football club in the Al-Ahsa region.",p2:"The club was officially recognized as a professional football club in the year of 1950. The club was formerly called \"Al-Badr\" before changing their name to \"Hajer\".",p3:"We are responsible of the design, construction and preparation of the club."},btn:b},proj8:{title:"King Khalid University Hospital",body:{p1:"Multi-disciplinary facility with general and subspecialty medical services that provides primary, secondary care & tertiary care to its patients.",p2:"It includes a designated outpatient and inpatient facility, state of the art surgical services and a fully equipped and staffed laboratory, radiology, and pharmacy services in addition to other support services and a dedicated home healthcare program.",p3:"We are responsible of the expansion of Kidney Dialysis Center and the expansion of emergency room at the University's Hospital."},btn:b},proj9:{title:"King Fahd Security College",body:{p1:"King Fahd Security College is an educational\u002Fmilitary institute part of the Saudi Ministry of Interior, College is located in eastern Riyadh,the capital of Saudi Arabia The faculty of educate and train students about the hardships of life and security to protect the security and stability of the country.",p2:"We provide the college with our experts technicians in maintenance and operations."},btn:b},proj10:{title:"Sattam bin Abdulaziz University",body:{p1:"Prince Sattam Bin Abdulaziz University was formerly known as Prince Salman Bin Abdulaziz University or the University of Al-Kharj . It is a Saudi University located in the city of Al-Kharj, Saudi Arabia.",p2:"It is under the supervision of the Ministry of Higher Education Saudi Arabia and managed by the Rector of the University, Dr. Abdul Rahman bin Mohammed Alasimi.",p3:"We provide the university campus, the university housing and the university hospital with our professional services in maintenance, operations and cleaning."},btn:b},proj11:{title:"King Fahad Road",body:{p1:"King Fahad Road, in the heart of Riyadh city.",p2:"Our professionals in Maintenance and Operations keeps the long crowded street clean and in a great condition."},btn:b},proj12:{title:"Diplomatic Quarter",body:{p1:"The DQ was originally built to accommodate the all the foreign embassy staff and diplomats, but nowadays anyone can live there in one of the many residential areas.",p2:"The DQ is so large it’s almost like a small town right next to Riyadh. The diplomatic quarters can easily be reached from the city center within 10 minutes by following the Ouroba rd to its end.",p3:"We provide the DQ with our professionals in Maintenance and Operations."},btn:b}},lets:{body:"Let's Work Together!"},count:{body:"Happy Clients!"},clients:{title:"Our Clients",body:{p1:"Our clients, like our people are our most treasured assets. We nurture every client relationship with commitment, passion and integrity, which is the reason why most of our clients have been with us through the decade of our existence.",p2:"By building not just business relationships but really long-lasting partnerships with our clients, we get the opportunity to work closely with them.",p3:"This helps us to truly understand their unique business needs, and then we are able to customize our solutions and products to meet their long-term business goals.",p4:"It is with pride that we can cite as our clientele, some of the leaders in the kingdom. We are equally proud to have contributed to the growth of many emerging players in the industry.",p5:"We provide our clients with the highest quality maintenance,operations and constructions services and solutions, We operate policies of continual improvement for our products, processes and people in an effort to deliver exceptional solutions, services and client experience that exceed client expectations.",p6:"To do this, we strive for a balance between flexibility in the way we operate and a tight control to consistently meet client needs."}}},footer:{title:"Contact Us",info:{intro:"You can contact us on one or more of the below contact channels.",address:"18 Zubida Bint Jaafar St, Al Murabba - P.O. Box. 17563, Riyadh 11494, Kingdom of Saudi Arabia.",phone1:"+966 11 404 1919",phone2:"+966 11 405 3012",mail:e},form:{name:"Name",mNumber:"Mobile Number",mAddress:"Mail Address",msg:"Message",send:"Send Message",errors:{name:"This field is required.",mail:"Please enter a valid e-mail address.",mobile:"This field should contain numeric values only. Use double zeros (00) instead of the (+) symbol."}}},about:{story:{title:"Our Story",subTitle:"Saudi Building Technic is proud of its 40 years of heritage of service in Saudi Arabia Kingdom.",list:{li1:"From the establishment in 1978 (1398 AH), we have grown to 3000 experienced staff professionals distributed throughout our projects. Saudi Building Technic is recognized as one of the leading maintenance and constructions firms in the kingdom.",li2:"We are proud too, of our significant contribution to the development of the modern communities through our input towards a large number of building, transportation, water and sanitation, environment, health, and education projects.",li3:"By associating with leading and world-class partners, we have many iconic significant projects in our portfolio, having worked at times to fast-track schedules to meet important business targets for our clients.",li4:"As we steadily expand our geographic presence around the kingdom, we feel confident in the strong and experienced base we have to serve and support our operations."}},whyUs:{title:"Why Us!",list:{li1:"We respect our clients, and thank those who have trusted us with repeated business time after time and project after project.",li2:"We appreciate our dedicated staff and faithful colleagues whose talents have helped us to grow into the modern company we are today.",li3:"We respect the partner companies with which we have shared our growth and success, many being leaders in their sectors.",li4:"With you all we look forward to a focused future based on a proud past."},vision:{title:"Vision",body:"Our inspiration is our desire to be distinguished as regional leaders in maintenance, operations and constructions, our commitment is to maintain a long relationship with our clients."},mission:{title:"Mission",body:"To deliver excellence in maintenance, operations and constructions, to be bound by our commitment to our clients and to a sustainable world, and to go beyond expectations."}},sisters:{title:"Sister Companies",comp01:"AMOS",comp02:"Elevators",comp03:"HAKTCO",comp04:"UPMOC"},chairman:{title:"Chairman's Word",list:{li1:"SBTMC is built on a foundation of core values that are shared across each and every member of our team.",li2:"These core values assisted SBTMC grow into a national leader in maintenance and constructions services.",li3:"We understand that the most important items in our industry are safety, time, cost, and quality, while protecting our environment.",li4:"With this in mind SBTMC collaborates with our clients to deliver the best possible outcome.",li5:"We are driven to achieve a legacy through a synergistic approach, based on integrity and satisfaction, building unsurpassed relationships, creating innovative, and sustainable solutions."}},lets:{body:"QUALITY COMES FIRST!"}}}}}}}("إغلاق","Close","معرفة المزيد ...","Read More ...","info@buildingtek.com","BuildingTek"));