window.__NUXT__=(function(a,b,c,d){return {staticAssetsBase:"\u002F_nuxt\u002Fstatic\u002F1692188123",layout:"default",error:c,serverRendered:true,routePath:"\u002Far\u002Fservices",config:{_app:{basePath:"\u002F",assetsPath:"\u002F_nuxt\u002F",cdnURL:c}},__i18n:{langs:{ar:{nav:{title:"فـن المعمار للصيانة",home:"الرئيسية",services:"الخدمات",portfolio:"مشاريع",about:"عن الشركة"},heading:{first:{intro:"شــركة",title:"فـن المعمار السعودية",message1:"شركة فن المعمار السعودية للصيانة المحدودة",message2:"إحدى شركات مجموعة الخولي"},second:{intro:"ما يمكننا فعله؟",title:"نقوم بالبناء والصيانة",message:"إحدى الشركات الرائدة في مجال البناء والصيانة في المملكة"},third:{title:"شركة فن المعمار للإنشاءات",message:"نحن نبني أحلامك في الواقع ونحافظ عليها."},fourth:{title:"شركة فن المعمار السعودية للصيانة",message:"نقدم خدمات إحترافية في مجال صيانة المباني لمختلف القطاعات داخل المجتمع"}},intro:{title1:"الأعلى تقييماً",body1:"شركة فن المعمار، تعد واحدة من أعلى الشركات تقييماً في المملكة العربية السعودية.",title2:"أعلى جودة ممكنة",body2:"تحرص شركة فن المعمار السعودية للصيانة المحدودة على أن تقدم أعلى جودة ممكنة في جميع الأعمال الموكلة إليها. وذلك عن طريق إتباع أحدث الطرق في إدارة المشاريع والحرص على إقتناء أفضل الكفاءات.",title3:"أقل الأسعار",body3:"واحدة من أهم الأهداف لشركتنا هي تقديم الخدمات بأقل التكاليف مع الحفاظ على أعلى جودة ممكنة."},home:{services:{title:"خدمات الشركة",sections:{facility:{title:"إدارة المرافق وتشغيلها",body:"نقدم خدمات صيانة المباني لمختلف المرافق المجتمعية، والجامعات، والوزارات، والمتاحف، والمؤسسات العامة والخاصة، من خلال أعمال الصيانة المنتظمة والمجدولة، مما يمنح عملائنا خدمة كاملة وراحة البال.",btn:b},constructions:{title:"الإنشاءات وإعادة التأهيل",body:"نقدم خدمات الإنشاءات وخدمات إعادة التأهيل من خلال مهندسينا المؤهلون في جميع المجالات الهندسية، ولدينا سجل حافل في الإنشاءات في المملكة.",btn:b},electrical:{title:"خدمات قطاع الكهرباء",body:"نقدم خدمات الصيانة الكهربائية من خلال مهندسينا المحترفين والفنيين المهرة القادرين على أداء أعمال الصيانة الكهربائية بطريقة مناسبة لعملائنا.",btn:b},mechanical:{title:"الخدمات الميكانيكية",body:"نقدم خدمات الصيانة الميكانيكية من خلال مهندسينا المحترفين والفنيين المهرة القادرين على أداء أعمال الصيانة الميكانيكية بأفضل الطرق المناسبة لعملائنا.",btn:b},landscaping:{title:"تنسيق الحدائق",body1:"نقدم خدمات تنسيق الحدائق الكاملة بدأً من تصميم المشروع، والخدمات الخارجية، والداخلية إلى تركيب نظام الري. ويتم التعامل مع تصميم المناظر الطبيعية من قبل فريق متخصص من المحترفين الذين يقومون بإجراء تحليل كامل للموقع والتوصل إلى أمثل الحلول لتحويل منطقتك الخارجية إلى حديقة رائعة.",body2:" نستخدم جميع منتجات التسميد ومكافحة الآفات والمعدات الزراعية الحديثة وفقًا لأعلى المعايير التي يتطلبها القانون وجميع أعمالنا يتم الإشراف والموافقة عليها من قبل مهندسون مؤهلون تأهيلا عاليا. يضمن تركيب نظام الري لدينا أن تحصل جميع نباتاتك الخارجية على الكمية المناسبة من الماء في أفضل وقت ممكن.",btn:b},pool:{title:"صيانة حمامات السباحة",body:"نقدم خدمات تنظيف حمامات السباحة في القطاعين العام والخاص. نستخدم أفضل منتجات ومعدات التنظيف. مع فنيينا المحترفون، يمكنك أن تطمئن إلى أن حمام السباحة الخاص بك نظيف دائمًا ويحتوي على أفضل المواد الكيميائية للتنظيف، وأفضل معدات النظافة. نقدم أيضًا خدمات الصيانة والتجديد لحمامات السباحة لجعلها تبدو أفضل دائمًا.",btn:b}}},projects:{title:"أحدث المشروعات",types:{all:"مشروعاتنا",one:"مشروعات الصيانة",two:"مشروعات الإنشاءات"},proj1:{title:"جامعة الإمام",body:{p1:"تأسست عام 1953. وفي عام 1974 تم منحها صفة الجامعة بمرسوم ملكي. تضم الجامعة 10 كليات. تضم حاليًا أكثر من 24000 طالب و 1300 عضو هيئة تدريس ولديها معاهد إسلامية متوسطة وثانوية في المدن الرئيسية في المملكة العربية السعودية وإندونيسيا وجيبوتي واليابان.",p2:"تهدف الجامعة كمؤسسة ثقافية وفكرية إلى تعزيز تقديم المعرفة للدراسات الجامعية والدراسات العليا.",p3:"من أهم الأهداف تعزيز الترجمة والنشر حيث تمتلك الجامعة دار طباعة خاصة بها. علاوة على ذلك، فإن الجامعة تحقق أهدافها في إطار الشريعة الإسلامية.",p4:"تعتبر الجامعة من أكبر مشاريعنا في المملكة العربية السعودية، فهي تحتوي على أكثر من 2000 موظف من موظفينا المحترفون، عمال النظافة، الفنيين، المهندسين، المحاسبين ، ...",p5:"نحن مسؤولون عن صيانة وتشغيل وتنظيف الحرم الجامعي."},btn:a},proj2:{title:d,body:{p1:"جامعة حكومية تأسست عام 2006. ويرأسها الأستاذ الدكتور سعد بن محمد الحركي.",p2:"يقع الحرم الرئيسي في العقيق على بعد 25 كم من مدينة الباحة. كما تقع مواقع أخرى للحرم الجامعي في المخواة والمندق وبلجرشي.",p3:"أرض الحرم الرئيسي في العقيق تشغل حوالي 6.7 كيلومتر مربع.",p4:"نحن نخدم شركة الباحة من خلال موظفينا المحترفين، كمزودي الصيانة والعمليات."},btn:a},proj3:{title:"مركز الملك عبدالعزيز التاريخي",body:{p1:"مركز الملك عبد العزيز التاريخي هو منطقة في مدينة الرياض ، عاصمة المملكة العربية السعودية.",p2:"وهو 'المركز التاريخي' للمدينة حيث يقع إلى الجنوب حول قلعة المصمك ومسجد الجمعة الرئيسي في منطقة الديرة.",p3:"أصل مركز الملك عبد العزيز التاريخي هو المجمع السابق لقصر المربع الذي شيده الملك عبد العزيز عام 1936\u002F1937 على بعد كيلومتر ونصف إلى الشمال من المدينة القديمة وخارج ما كان آنذاك، أسوار المدينة.",p4:"يقوم مهندسونا وفنيونا وعمال النظافة لدينا بتزويد المركز بخدمات الصيانة والتشغيل."},btn:a},proj4:{title:"مكتبة الملك فهد الوطنية",body:{p1:"تأسست المكتبة عام 1990 ، ومقرها مدينة الرياض. ومعروفة بتصميم المبنى الرائع والكتب النادرة.",p2:"لسنوات عديدة خدمنا المكتبة بخبراتنا في مجال الصيانة والعمليات."},btn:a},proj5:{title:"الرئاسة العامة لرعاية الشباب",body:{p1:"قامت الرئاسة العامة لرعاية الشباب (GPYW) برعاية مجموعة كبيرة من الأنشطة الثقافية.",p2:"وشملت هذه الأنشطة الفنون، والحرف ، ونوادي الأدب والدراما والفلكلور.",p3:"لعبت الرئاسة العامة لرعاية الشباب دورًا نشطًا في الأحداث الثقافية الوطنية والدولية ، بما في ذلك المعارض الفنية ومسابقات الخط والشعر والمقالات.",p4:"نحن مسؤولون عن تصميم وبناء التوسعة للنادي الاجتماعي في منطقة سكن أعضاء هيئة التدريس."},btn:a},proj6:{title:"إستاد الأمير عبد الله الفيصل",body:{p1:"يقع الملعب جنوب شرق مدينة جدة بين جامعة الملك عبد العزيز والمدينة الصناعية (حي الوزيرية).",p2:"تم بناء الاستاد في عام 1970 ، ويتسع لحوالي 27000 متفرج ، وهو جزء من مجمع رياضي بلدي يضم ساحة داخلية ومركزًا للألعاب المائية.",p3:"يتكون الملعب من مدرجين رئيسيين. الجناح الغربي المغطى ، مجالس بالكامل وبه صندوق مغلق لكبار الشخصيات وأجنحة إعلامية في منبر مركزي.",p4:"الحامل الشرقي على شكل قوس متدرج ، ومنحنيات على طول مسار الجري. توجد لوحة نتائج كبيرة واحدة على الحافة الشمالية من الجناح الشرقي.",p5:"نحن مسؤولون عن تصميم التوسعة الجديدة للملعب وبنائها من خلال مهندسينا وعمالنا المحترفين."},btn:a},proj7:{title:"نادي هجر الرياضي",body:{p1:"تأسس نادي هجر في مدينة الهفوف التابعة لمحافظة الأحساء عام 1948 وهو أقدم نادي كرة قدم للمحترفين في منطقة الأحساء.",p2:"تم الاعتراف بالنادي رسميًا كنادي كرة قدم للمحترفين في عام 1950. كان النادي يُطلق عليه سابقًا 'نادي البدر' قبل تغيير اسمه إلى 'هجر'.",p3:"نحن مسؤولون عن تصميم وبناء وإعداد النادي."},btn:a},proj8:{title:"مستشفى الملك خالد الجامعي",body:{p1:"مرفق متعدد التخصصات في الخدمات الطبية العامة والمتخصصة التي تقدم الرعاية الأولية والثانوية لمرضاها.",p2:"ويشمل مرفقًا مخصصًا للمرضى، وأحدث الخدمات الجراحية ومختبرًا مجهزًا بالكامل وموظفين، وخدمات الأشعة والصيدلة بالإضافة إلى خدمات الدعم الأخرى وبرنامج الرعاية الصحية المنزلية المخصص.",p3:"نحن مسؤولون عن توسيع مركز غسيل الكلى وتوسيع غرفة الطوارئ في مستشفى الجامعة."},btn:a},proj9:{title:"كلية الملك فهد الأمنية",body:{p1:"كلية الملك فهد الأمنية معهد تعليمي \u002F عسكري تابع لوزارة الداخلية السعودية ، تقع الكلية في شرق الرياض. كلية تثقيف وتدريب للطلاب حفاظاً على الأمن والاستقرار في البلاد.",p2:"نوفر للكلية فنيين خبراء في الصيانة والعمليات."},btn:a},proj10:{title:"جامعة الأمير سطام بن عبد العزيز",body:{p1:"كانت جامعة الأمير سطام بن عبد العزيز تعرف سابقًا باسم جامعة الأمير سلمان بن عبد العزيز أو جامعة الخرج. هي جامعة سعودية تقع في مدينة الخرج.",p2:"وهي تحت إشراف وزارة التعليم العالي بالمملكة العربية السعودية ويديرها الدكتور عبد الرحمن بن محمد العصيمي.",p3:"نوفر للحرم الجامعي والسكن الجامعي والمستشفى الجامعي خدماتنا المهنية في الصيانة والتشغيل والنظافة."},btn:a},proj11:{title:"طريق الملك فهد",body:{p1:"يقع طريق الملك فهد في قلب مدينة الرياض.",p2:"يحافظ المتخصصون لدينا في الصيانة والعمليات على نظافة الشوارع الطويلة المزدحمة وأن تكون دائماً في حالة جيدة."},btn:a},proj12:{title:"الحي الدبلوماسي",body:{p1:"تم بناء الحي الدبلوماسي في الأصل لاستيعاب جميع موظفي السفارات الأجنبية والدبلوماسيين، ولكن في الوقت الحاضر يمكن لأي شخص العيش هناك في واحدة من العديد من المناطق السكنية.",p2:"الحي كبير جدًا لدرجة أنه يشبه مدينة صغيرة بجوار الرياض. يمكن الوصول إلى الأحياء الدبلوماسية بسهولة من وسط المدينة في غضون 10 دقائق باتباع طريق العروبة حتى نهايته.",p3:"نوفر للحي الدبلوماسي كافة المتخصصين لدينا في الصيانة والعمليات."},btn:a}},projectsFinancials:{title:"البيانات المالية للمشروعات الجديدة",first:{title:d,secondaryTitle:"شركة فن المعمار للصيانة المحدودة تفوز بعقد تشغيل وصيانة ونظافة جامعة الباحة",body:"استمرارا في تميز الشركة في تقديم خدمات إدارة المرافق وخصوصا في مجال الجامعات والتعليم العالي، وقعت الشركة عقدأ يمتد لمدة ثلاث سنوات مع جامعة الباحة لتقديم خدمات التشغيل والصيانة والنظافة لجميع مرافق الجامعة الأكاديمية والسكنية والإدارية وذلك بقيمة 44.6 مليون ريال، وسوف تبدأ عمليات المشروع اعتبارا من شهر أغسطس 2023 الحالي."},second:{title:"جامعة الباحة بالمحافظات",secondaryTitle:"شركة فن المعمار للصيانة المحدودة تفوز بعقد تشغيل وصيانة ونظافة فروع جامعة الباحة بالمحافظات ",body:"وقعت الشركة عقدأ يمتد لمدة ثلاث سنوات مع جامعة الباحة لتقديم خدمات التشغيل والصيانة والنظافة لفروع الجامعة بالمحافظات وذلك بقيمة  44.3  مليون ريال، وسوف تبدأ عمليات المشروع اعتبارا من شهر أغسطس 2023 الحالي."},third:{title:"مجمع ساتكو السكني في نيوم",secondaryTitle:"شركة فن المعمار بالتضامن مع شركتين شقيقة تفوز بعقد ادارة المرافق في مجمع ساتكو السكني في نيوم ",body:"تقود شركة فن المعمار تضامنا مع شركتي سكاي هاي وشركة المشروعات المتحدة لإدارة جميع مرافق مجمع ساتكو السكنية والترفيهية والطبية وخدمات ادارة وتشغيل محطة الصرف ومركز الإطفاء وتقديم خدمات الأمن والسلامة وجميع مرافق الخدمات المساندة الأخرى وذلك بمدينة نيوم. علما بأن مدة العقد هي خمس سنوات بالاضافة الى خمس سنوات أخرى اختيارية. وتعتمد قيمة المشروع على عدد الساكنين اللذين يتم تقديم الخدمة لهم حيث أن نسبة الإشغال قد وصلت الى 5000 ضيف من السعة الإجمالية التي تبلغ 10,000 شخص. "}},lets:{body:"فلنبدأ بالعمل معاً!"},count:{body:"عملاء راضون!"},clients:{title:"عملائنا",body:{p1:"عملاؤنا، مثل موظفينا، هم أغلى ما نملك. نحن نرعى علاقة كل عميل بالالتزام والعاطفة والنزاهة، وهذا هو السبب في أن معظم عملائنا كانوا معنا على مدار عقود من وجودنا.",p2:"من خلال بناء ليس فقط علاقات تجارية ولكن شراكات طويلة الأمد مع عملائنا، نحصل على فرصة للعمل عن كثب معهم.",p3:"يساعدنا ذلك على فهم احتياجات أعمالهم الفريدة، ومن ثم نتمكن من تخصيص حلولنا ومنتجاتنا لتلبية أهداف أعمالهم طويلة الأجل.",p4:"بكل فخر يمكننا الاستشهاد بعملائنا، والذين يعدون من أكبر الكيانات في المملكة. نحن فخورون جداً لأننا ساهمنا في نمو العديد من هذه الكيانات.",p5:"نحن نقدم لعملائنا أعلى جودة من خدمات وحلول الصيانة والعمليات والإنشاءات، ونعمل على سياسات التحسين المستمر لمنتجاتنا وعملياتنا والأفراد لدينا في محاولة لتقديم حلول وخدمات استثنائية وتجربة تتجاوز توقعات العميل.",p6:"للقيام بذلك، نسعى جاهدين لتحقيق التوازن بين المرونة في طريقة عملنا والتحكم الصارم لتلبية احتياجات العملاء باستمرار."}}},footer:{title:"تواصل معنا",info:{intro:"يمكنك التواصل معنا على أي من قنوات الاتصال أدناه.",address:"18 شارع زبيدة بنت جعفر، حي المربع، الرياض، المملكة العربية السعودية",phone1:"966114041919+",phone2:"966114053012+",mail:"info@buildingtek.com"},form:{name:"الإسم",mNumber:"رقم الهاتف",mAddress:"عنوان البريد الإلكتروني",msg:"الرسالة",send:"إرســـال",errors:{name:"هذا الحقل مطلوب.",mail:"برجاء إدخال بريد إلكتروني صحيح.",mobile:"هذا الحقل يجب أن يحتوي على أرقام فقط، إستخدم صفرين بدلاً من علامة (+)."}}},about:{story:{title:"قصــتنا",subTitle:"تفتخر شركتنا بـ 40 عامًا من الخدمة في المملكة العربية السعودية.",list:{li1:"منذ التأسيس في عام 1978 (1398 هـ)، نمت أعداد الموظفين المحترفين لدينا إلى 3000 موظف موزعون في جميع مشاريعنا. تعتبر شركتنا من الشركات الرائدة في مجال الصيانة والإنشاءات في المملكة.",li2:"نحن فخورون أيضًا بمساهمتنا الكبيرة في تطوير مجتمعنا الحديث من خلال مساهمتنا في عدد كبير من مشاريع البناء والنقل والمياه والصرف الصحي والبيئة والصحة والتعليم.",li3:"من خلال الارتباط بشركاء رائدين وعالميين، لدينا العديد من المشاريع المهمة والمميزة، حيث عملنا في بعض الأحيان لتسريع الجداول الزمنية لتلبية الأهداف الأهم لعملائنا.",li4:"مع قيامنا بتوسيع وجودنا الجغرافي بشكل كبير في جميع أنحاء المملكة، نشعر بالثقة في القاعدة القوية والخبرة التي تحصلنا عليها لخدمة ودعم عملياتنا."}},whyUs:{title:"لماذا تختارنا!",list:{li1:"نحن نحترم عملائنا، ونقدر أولئك الذين وثقوا بنا من خلال عمل متكرر مرة بعد مرة ومشروع بعد مشروع.",li2:"نحن نقدر موظفينا المتفانين وزملائنا المخلصين الذين ساعدتنا مواهبهم على النمو لنصبح الشركة الحديثة التي نحن عليها اليوم.",li3:"نحن نحترم الشركات الشريكة التي شاركتنا نمونا ونجاحنا، والعديد منها قادة في قطاعاتهم.",li4:"معكم جميعًا نتطلع إلى مستقبل أفضل قائم على ماضٍ عريق."},vision:{title:"رؤيتــنا",body:"مصدر إلهامنا هو رغبتنا في التميز كقادة إقليميين في الصيانة والعمليات والإنشاءات، ومهمتنا هي الحفاظ على علاقة طويلة الأمد مع عملائنا."},mission:{title:"مهمتنــا",body:"تقديم التميز في مجال الصيانة والعمليات والإنشاءات، والحفاظ على التزامنا إتجاه عملاؤنا، وتجاوز توقعاتهم."}},sisters:{title:"شركات شقيقة",comp01:"العربية للمشروعات",comp02:"فن المعمار للمصاعد",comp03:"مؤسسة حمزة الخولي",comp04:"الشركة المتحدة للمشروعات"},chairman:{title:"كلمة الرئيس",list:{li1:"تم بناء شركتنا على أساس القيم التي يتم مشاركتها عبر كل عضو من أعضاء فريقنا.",li2:"ساعدت هذه القيم الأساسية على نمو الشركة، لتصبح شركة وطنية رائدة في خدمات الصيانة والإنشاءات.",li3:"نحن نتفهم أن أهم العناصر في صناعتنا هي السلامة والوقت والتكلفة والجودة، مع حماية بيئتنا والحفاظ عليها.",li4:"مع وضع ذلك في الاعتبار، تتعاون شركتنا مع عملائنا لتقديم أفضل نتيجة ممكنة.",li5:"نحن مدفوعون لتحقيق النجاح من خلال التعاون، القائم على النزاهة والرضا، وبناء علاقات غير مسبوقة، وخلق حلول مبتكرة ومستدامة."}},lets:{body:"الجودة تأتي أولاً!"}}}}}}}("إغلاق","معرفة المزيد ...",null,"جامعة الباحة"));